import { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import { Upload, ChartLine, Sparkles, ArrowRight, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';

interface OnboardingStep {
    icon: React.ElementType;
    titleKey: string;
    descKey: string;
    color: string;
}

const steps: OnboardingStep[] = [
    {
        icon: Upload,
        titleKey: 'onboarding.step1Title',
        descKey: 'onboarding.step1Desc',
        color: 'from-blue-500 to-cyan-500',
    },
    {
        icon: ChartLine,
        titleKey: 'onboarding.step2Title',
        descKey: 'onboarding.step2Desc',
        color: 'from-green-500 to-emerald-500',
    },
    {
        icon: Sparkles,
        titleKey: 'onboarding.step3Title',
        descKey: 'onboarding.step3Desc',
        color: 'from-violet-500 to-purple-500',
    },
];

interface OnboardingProps {
    onComplete: () => void;
}

export const Onboarding = ({ onComplete }: OnboardingProps) => {
    const { t } = useTranslation();
    const [currentStep, setCurrentStep] = useState(0);
    const [isVisible, setIsVisible] = useState(true);

    const handleNext = () => {
        if (currentStep < steps.length - 1) {
            setCurrentStep(currentStep + 1);
        } else {
            handleComplete();
        }
    };

    const handleSkip = () => {
        handleComplete();
    };

    const handleComplete = () => {
        setIsVisible(false);
        localStorage.setItem('onboarding_completed', 'true');
        setTimeout(onComplete, 300);
    };

    const step = steps[currentStep];
    const StepIcon = step.icon;

    return (
        <AnimatePresence>
            {isVisible && (
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    className="fixed inset-0 z-50 flex items-center justify-center bg-background/95 backdrop-blur-sm"
                >
                    {/* Skip button */}
                    <button
                        onClick={handleSkip}
                        className="absolute top-4 right-4 p-2 text-muted-foreground hover:text-foreground transition-colors"
                    >
                        <X className="h-5 w-5" />
                    </button>

                    <div className="max-w-lg mx-auto px-6 text-center">
                        {/* Progress dots */}
                        <div className="flex justify-center gap-2 mb-8">
                            {steps.map((_, idx) => (
                                <div
                                    key={idx}
                                    className={cn(
                                        'h-2 rounded-full transition-all duration-300',
                                        idx === currentStep
                                            ? 'w-8 bg-primary'
                                            : idx < currentStep
                                                ? 'w-2 bg-primary/50'
                                                : 'w-2 bg-muted'
                                    )}
                                />
                            ))}
                        </div>

                        {/* Icon */}
                        <motion.div
                            key={currentStep}
                            initial={{ scale: 0, rotate: -180 }}
                            animate={{ scale: 1, rotate: 0 }}
                            transition={{ type: 'spring', duration: 0.5 }}
                            className={cn(
                                'mx-auto mb-8 h-24 w-24 rounded-3xl flex items-center justify-center',
                                `bg-gradient-to-br ${step.color}`
                            )}
                        >
                            <StepIcon className="h-12 w-12 text-white" />
                        </motion.div>

                        {/* Content */}
                        <motion.div
                            key={`content-${currentStep}`}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: 0.2 }}
                        >
                            <h2 className="text-2xl font-bold mb-4">{t(step.titleKey)}</h2>
                            <p className="text-muted-foreground mb-8 leading-relaxed">
                                {t(step.descKey)}
                            </p>
                        </motion.div>

                        {/* Actions */}
                        <div className="flex flex-col gap-3">
                            <Button onClick={handleNext} size="lg" className="w-full gap-2">
                                {currentStep === steps.length - 1
                                    ? t('onboarding.getStarted')
                                    : t('common.next')}
                                <ArrowRight className="h-4 w-4" />
                            </Button>
                            {currentStep < steps.length - 1 && (
                                <Button
                                    variant="ghost"
                                    onClick={handleSkip}
                                    className="text-muted-foreground"
                                >
                                    {t('onboarding.skip')}
                                </Button>
                            )}
                        </div>
                    </div>
                </motion.div>
            )}
        </AnimatePresence>
    );
};

// Hook to manage onboarding state
export const useOnboarding = () => {
    const [showOnboarding, setShowOnboarding] = useState(false);
    const [isFirstVisit, setIsFirstVisit] = useState(false);

    useEffect(() => {
        const completed = localStorage.getItem('onboarding_completed');
        if (!completed) {
            setShowOnboarding(true);
            setIsFirstVisit(true);
        }
    }, []);

    const completeOnboarding = () => {
        setShowOnboarding(false);
        localStorage.setItem('onboarding_completed', 'true');
    };

    const resetOnboarding = () => {
        localStorage.removeItem('onboarding_completed');
        setShowOnboarding(true);
    };

    return {
        showOnboarding,
        isFirstVisit,
        completeOnboarding,
        resetOnboarding,
    };
};

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import {
    Upload,
    TrendingUp,
    Bell,
    Share2,
    ChevronRight,
    ChevronLeft,
    CheckCircle2,
    Sparkles,
    ArrowRight,
} from 'lucide-react';
import { cn } from '@/lib/utils';

interface OnboardingStep {
    id: string;
    icon: React.ElementType;
    iconBg: string;
    iconColor: string;
    title: string;
    description: string;
    feature?: string;
}

const onboardingSteps: OnboardingStep[] = [
    {
        id: 'upload',
        icon: Upload,
        iconBg: 'bg-primary/10',
        iconColor: 'text-primary',
        title: 'Fa√ßa upload dos seus exames',
        description: 'Arraste PDFs ou imagens de exames para a √°rea de upload. Nossa IA extrai automaticamente os resultados.',
        feature: 'Suporte a PDF, PNG, JPG e c√¢mera',
    },
    {
        id: 'trending',
        icon: TrendingUp,
        iconBg: 'bg-blue-500/10',
        iconColor: 'text-blue-500',
        title: 'Acompanhe suas tend√™ncias',
        description: 'Veja a evolu√ß√£o dos seus marcadores ao longo do tempo com gr√°ficos interativos e insights personalizados.',
        feature: 'An√°lise de IA inclu√≠da',
    },
    {
        id: 'alerts',
        icon: Bell,
        iconBg: 'bg-amber-500/10',
        iconColor: 'text-amber-500',
        title: 'Receba alertas inteligentes',
        description: 'Seja notificado quando seus valores estiverem fora do normal ou quando for hora de repetir um exame.',
        feature: 'Push, email e lembretes',
    },
    {
        id: 'share',
        icon: Share2,
        iconBg: 'bg-emerald-500/10',
        iconColor: 'text-emerald-500',
        title: 'Compartilhe com seu m√©dico',
        description: 'Gere links seguros para compartilhar seus exames com profissionais de sa√∫de.',
        feature: 'Links expir√°veis e seguros',
    },
];

interface OnboardingTourProps {
    onComplete: () => void;
    onSkip?: () => void;
}

/**
 * Tour de onboarding com steps animados
 */
export const OnboardingTour = ({ onComplete, onSkip }: OnboardingTourProps) => {
    const [currentStep, setCurrentStep] = useState(0);
    const [direction, setDirection] = useState(1);

    const step = onboardingSteps[currentStep];
    const progress = ((currentStep + 1) / onboardingSteps.length) * 100;
    const isLastStep = currentStep === onboardingSteps.length - 1;

    const goToNext = () => {
        if (isLastStep) {
            onComplete();
        } else {
            setDirection(1);
            setCurrentStep(prev => prev + 1);
        }
    };

    const goToPrev = () => {
        if (currentStep > 0) {
            setDirection(-1);
            setCurrentStep(prev => prev - 1);
        }
    };

    const slideVariants = {
        enter: (direction: number) => ({
            x: direction > 0 ? 300 : -300,
            opacity: 0,
        }),
        center: {
            x: 0,
            opacity: 1,
        },
        exit: (direction: number) => ({
            x: direction > 0 ? -300 : 300,
            opacity: 0,
        }),
    };

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-background/80 backdrop-blur-sm">
            <Card className="w-full max-w-lg overflow-hidden">
                <CardContent className="p-0">
                    {/* Progress bar */}
                    <div className="p-4 border-b border-border">
                        <div className="flex items-center justify-between mb-2">
                            <span className="text-sm text-muted-foreground">
                                Passo {currentStep + 1} de {onboardingSteps.length}
                            </span>
                            {onSkip && (
                                <Button variant="ghost" size="sm" onClick={onSkip}>
                                    Pular tour
                                </Button>
                            )}
                        </div>
                        <Progress value={progress} className="h-1.5" />
                    </div>

                    {/* Step content */}
                    <div className="relative h-[320px] overflow-hidden">
                        <AnimatePresence mode="wait" custom={direction}>
                            <motion.div
                                key={step.id}
                                custom={direction}
                                variants={slideVariants}
                                initial="enter"
                                animate="center"
                                exit="exit"
                                transition={{ type: 'spring', stiffness: 300, damping: 30 }}
                                className="absolute inset-0 p-8 flex flex-col items-center text-center"
                            >
                                {/* Icon */}
                                <motion.div
                                    initial={{ scale: 0.5, opacity: 0 }}
                                    animate={{ scale: 1, opacity: 1 }}
                                    transition={{ delay: 0.2 }}
                                    className={cn(
                                        'h-20 w-20 rounded-2xl flex items-center justify-center mb-6',
                                        step.iconBg
                                    )}
                                >
                                    <step.icon className={cn('h-10 w-10', step.iconColor)} />
                                </motion.div>

                                {/* Title */}
                                <motion.h2
                                    initial={{ y: 20, opacity: 0 }}
                                    animate={{ y: 0, opacity: 1 }}
                                    transition={{ delay: 0.3 }}
                                    className="text-xl font-bold mb-3"
                                >
                                    {step.title}
                                </motion.h2>

                                {/* Description */}
                                <motion.p
                                    initial={{ y: 20, opacity: 0 }}
                                    animate={{ y: 0, opacity: 1 }}
                                    transition={{ delay: 0.4 }}
                                    className="text-muted-foreground mb-4"
                                >
                                    {step.description}
                                </motion.p>

                                {/* Feature tag */}
                                {step.feature && (
                                    <motion.div
                                        initial={{ y: 20, opacity: 0 }}
                                        animate={{ y: 0, opacity: 1 }}
                                        transition={{ delay: 0.5 }}
                                        className="flex items-center gap-2 px-3 py-1.5 rounded-full bg-primary/10"
                                    >
                                        <Sparkles className="h-4 w-4 text-primary" />
                                        <span className="text-sm font-medium text-primary">
                                            {step.feature}
                                        </span>
                                    </motion.div>
                                )}
                            </motion.div>
                        </AnimatePresence>
                    </div>

                    {/* Step indicators */}
                    <div className="flex items-center justify-center gap-2 py-4">
                        {onboardingSteps.map((_, index) => (
                            <button
                                key={index}
                                onClick={() => {
                                    setDirection(index > currentStep ? 1 : -1);
                                    setCurrentStep(index);
                                }}
                                className={cn(
                                    'h-2 rounded-full transition-all',
                                    index === currentStep
                                        ? 'w-8 bg-primary'
                                        : 'w-2 bg-muted-foreground/30 hover:bg-muted-foreground/50'
                                )}
                                aria-label={`Ir para passo ${index + 1}`}
                            />
                        ))}
                    </div>

                    {/* Navigation */}
                    <div className="flex items-center justify-between p-4 border-t border-border">
                        <Button
                            variant="ghost"
                            onClick={goToPrev}
                            disabled={currentStep === 0}
                            className="gap-2"
                        >
                            <ChevronLeft className="h-4 w-4" />
                            Anterior
                        </Button>

                        <Button onClick={goToNext} className="gap-2">
                            {isLastStep ? (
                                <>
                                    <CheckCircle2 className="h-4 w-4" />
                                    Come√ßar
                                </>
                            ) : (
                                <>
                                    Pr√≥ximo
                                    <ChevronRight className="h-4 w-4" />
                                </>
                            )}
                        </Button>
                    </div>
                </CardContent>
            </Card>
        </div>
    );
};

/**
 * Hook para controlar exibi√ß√£o do onboarding
 */
export const useOnboarding = () => {
    const STORAGE_KEY = 'health-beacon-onboarding-complete';
    const [showOnboarding, setShowOnboarding] = useState(false);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const completed = localStorage.getItem(STORAGE_KEY);
        if (!completed) {
            setShowOnboarding(true);
        }
        setIsLoading(false);
    }, []);

    const completeOnboarding = () => {
        localStorage.setItem(STORAGE_KEY, 'true');
        setShowOnboarding(false);
    };

    const resetOnboarding = () => {
        localStorage.removeItem(STORAGE_KEY);
        setShowOnboarding(true);
    };

    return {
        showOnboarding,
        isLoading,
        completeOnboarding,
        resetOnboarding,
    };
};

import { Component, ErrorInfo, ReactNode } from 'react';
import { AlertCircle, RefreshCw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';

interface Props {
    children: ReactNode;
    fallback?: ReactNode;
}

interface State {
    hasError: boolean;
    error: Error | null;
    errorInfo: ErrorInfo | null;
}

export class ErrorBoundary extends Component<Props, State> {
    public state: State = {
        hasError: false,
        error: null,
        errorInfo: null,
    };

    public static getDerivedStateFromError(error: Error): State {
        return { hasError: true, error, errorInfo: null };
    }

    public componentDidCatch(error: Error, errorInfo: ErrorInfo) {
        console.error('ErrorBoundary caught an error:', error, errorInfo);

        this.setState({
            error,
            errorInfo,
        });

        // TODO: Send to error tracking service (Sentry)
        if (window.Sentry) {
            window.Sentry.captureException(error, {
                contexts: {
                    react: {
                        componentStack: errorInfo.componentStack,
                    },
                },
            });
        }
    }

    private handleReset = () => {
        this.setState({
            hasError: false,
            error: null,
            errorInfo: null,
        });
    };

    public render() {
        if (this.state.hasError) {
            if (this.props.fallback) {
                return this.props.fallback;
            }

            return (
                <div className="flex items-center justify-center min-h-screen p-4 bg-background">
                    <Card className="max-w-lg w-full">
                        <CardHeader>
                            <div className="flex items-center gap-3">
                                <div className="h-12 w-12 rounded-xl bg-destructive/10 flex items-center justify-center">
                                    <AlertCircle className="h-6 w-6 text-destructive" />
                                </div>
                                <div>
                                    <CardTitle>Ops! Algo deu errado</CardTitle>
                                    <CardDescription>
                                        Ocorreu um erro inesperado na aplica√ß√£o
                                    </CardDescription>
                                </div>
                            </div>
                        </CardHeader>
                        <CardContent className="space-y-4">
                            {process.env.NODE_ENV === 'development' && this.state.error && (
                                <div className="rounded-lg bg-muted p-4">
                                    <p className="text-sm font-mono text-destructive mb-2">
                                        {this.state.error.toString()}
                                    </p>
                                    {this.state.errorInfo && (
                                        <details className="text-xs font-mono text-muted-foreground">
                                            <summary className="cursor-pointer hover:text-foreground">
                                                Stack trace
                                            </summary>
                                            <pre className="mt-2 whitespace-pre-wrap">
                                                {this.state.errorInfo.componentStack}
                                            </pre>
                                        </details>
                                    )}
                                </div>
                            )}
                            <div className="flex gap-3">
                                <Button onClick={this.handleReset} className="flex-1">
                                    <RefreshCw className="h-4 w-4 mr-2" />
                                    Tentar novamente
                                </Button>
                                <Button
                                    variant="outline"
                                    onClick={() => window.location.reload()}
                                    className="flex-1"
                                >
                                    Recarregar p√°gina
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                </div>
            );
        }

        return this.props.children;
    }
}

// Extend Window interface for Sentry
declare global {
    interface Window {
        Sentry?: {
            captureException: (error: Error, context?: any) => void;
        };
    }
}

import { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import {
    CheckCircle2,
    AlertCircle,
    XCircle,
    Info,
    X,
    Loader2
} from 'lucide-react';
import { cn } from '@/lib/utils';

type FeedbackType = 'success' | 'error' | 'warning' | 'info' | 'loading';

interface FeedbackItem {
    id: string;
    type: FeedbackType;
    title: string;
    message?: string;
    duration?: number;
    action?: {
        label: string;
        onClick: () => void;
    };
}

interface UseFeedbackReturn {
    feedbacks: FeedbackItem[];
    showFeedback: (feedback: Omit<FeedbackItem, 'id'>) => string;
    dismissFeedback: (id: string) => void;
    clearAll: () => void;
    success: (title: string, message?: string) => string;
    error: (title: string, message?: string) => string;
    warning: (title: string, message?: string) => string;
    info: (title: string, message?: string) => string;
    loading: (title: string, message?: string) => string;
}

const defaultDurations: Record<FeedbackType, number> = {
    success: 3000,
    error: 5000,
    warning: 4000,
    info: 4000,
    loading: 0, // Loading doesn't auto-dismiss
};

/**
 * Hook para gerenciar feedback de a√ß√µes do usu√°rio
 */
export const useFeedback = (): UseFeedbackReturn => {
    const [feedbacks, setFeedbacks] = useState<FeedbackItem[]>([]);

    const showFeedback = useCallback((feedback: Omit<FeedbackItem, 'id'>): string => {
        const id = `feedback-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
        const duration = feedback.duration ?? defaultDurations[feedback.type];

        setFeedbacks(prev => [...prev, { ...feedback, id }]);

        if (duration > 0) {
            setTimeout(() => {
                setFeedbacks(prev => prev.filter(f => f.id !== id));
            }, duration);
        }

        return id;
    }, []);

    const dismissFeedback = useCallback((id: string) => {
        setFeedbacks(prev => prev.filter(f => f.id !== id));
    }, []);

    const clearAll = useCallback(() => {
        setFeedbacks([]);
    }, []);

    const success = useCallback((title: string, message?: string) => {
        return showFeedback({ type: 'success', title, message });
    }, [showFeedback]);

    const error = useCallback((title: string, message?: string) => {
        return showFeedback({ type: 'error', title, message });
    }, [showFeedback]);

    const warning = useCallback((title: string, message?: string) => {
        return showFeedback({ type: 'warning', title, message });
    }, [showFeedback]);

    const info = useCallback((title: string, message?: string) => {
        return showFeedback({ type: 'info', title, message });
    }, [showFeedback]);

    const loading = useCallback((title: string, message?: string) => {
        return showFeedback({ type: 'loading', title, message });
    }, [showFeedback]);

    return {
        feedbacks,
        showFeedback,
        dismissFeedback,
        clearAll,
        success,
        error,
        warning,
        info,
        loading,
    };
};

interface FeedbackContainerProps {
    feedbacks: FeedbackItem[];
    onDismiss: (id: string) => void;
    position?: 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left' | 'top-center' | 'bottom-center';
}

const iconMap: Record<FeedbackType, React.ElementType> = {
    success: CheckCircle2,
    error: XCircle,
    warning: AlertCircle,
    info: Info,
    loading: Loader2,
};

const colorMap: Record<FeedbackType, { bg: string; icon: string; border: string }> = {
    success: {
        bg: 'bg-status-healthy/10',
        icon: 'text-status-healthy',
        border: 'border-status-healthy/20',
    },
    error: {
        bg: 'bg-status-danger/10',
        icon: 'text-status-danger',
        border: 'border-status-danger/20',
    },
    warning: {
        bg: 'bg-status-warning/10',
        icon: 'text-status-warning',
        border: 'border-status-warning/20',
    },
    info: {
        bg: 'bg-primary/10',
        icon: 'text-primary',
        border: 'border-primary/20',
    },
    loading: {
        bg: 'bg-muted',
        icon: 'text-muted-foreground',
        border: 'border-border',
    },
};

const positionClasses: Record<NonNullable<FeedbackContainerProps['position']>, string> = {
    'top-right': 'top-4 right-4',
    'top-left': 'top-4 left-4',
    'bottom-right': 'bottom-4 right-4',
    'bottom-left': 'bottom-4 left-4',
    'top-center': 'top-4 left-1/2 -translate-x-1/2',
    'bottom-center': 'bottom-4 left-1/2 -translate-x-1/2',
};

/**
 * Container para exibir feedbacks animados
 */
export const FeedbackContainer = ({
    feedbacks,
    onDismiss,
    position = 'top-right'
}: FeedbackContainerProps) => {
    return (
        <div
            className={cn(
                'fixed z-50 flex flex-col gap-2 w-full max-w-sm pointer-events-none',
                positionClasses[position]
            )}
            aria-live="polite"
            aria-label="Notifica√ß√µes"
        >
            <AnimatePresence mode="popLayout">
                {feedbacks.map((feedback) => {
                    const Icon = iconMap[feedback.type];
                    const colors = colorMap[feedback.type];

                    return (
                        <motion.div
                            key={feedback.id}
                            layout
                            initial={{ opacity: 0, y: -20, scale: 0.95 }}
                            animate={{ opacity: 1, y: 0, scale: 1 }}
                            exit={{ opacity: 0, scale: 0.95, transition: { duration: 0.15 } }}
                            transition={{ type: 'spring', stiffness: 500, damping: 30 }}
                            className={cn(
                                'pointer-events-auto flex items-start gap-3 p-4 rounded-xl border shadow-lg',
                                'bg-card/95 backdrop-blur-sm',
                                colors.border
                            )}
                            role="alert"
                        >
                            <div className={cn('p-1.5 rounded-lg', colors.bg)}>
                                <Icon
                                    className={cn(
                                        'h-4 w-4',
                                        colors.icon,
                                        feedback.type === 'loading' && 'animate-spin'
                                    )}
                                />
                            </div>

                            <div className="flex-1 min-w-0">
                                <p className="text-sm font-medium text-foreground">
                                    {feedback.title}
                                </p>
                                {feedback.message && (
                                    <p className="text-sm text-muted-foreground mt-0.5">
                                        {feedback.message}
                                    </p>
                                )}
                                {feedback.action && (
                                    <button
                                        onClick={feedback.action.onClick}
                                        className={cn(
                                            'text-sm font-medium mt-2',
                                            colors.icon,
                                            'hover:underline focus:outline-none focus:underline'
                                        )}
                                    >
                                        {feedback.action.label}
                                    </button>
                                )}
                            </div>

                            {feedback.type !== 'loading' && (
                                <button
                                    onClick={() => onDismiss(feedback.id)}
                                    className="p-1 rounded-md hover:bg-muted transition-colors"
                                    aria-label="Fechar notifica√ß√£o"
                                >
                                    <X className="h-4 w-4 text-muted-foreground" />
                                </button>
                            )}
                        </motion.div>
                    );
                })}
            </AnimatePresence>
        </div>
    );
};

import { useState, useEffect } from 'react';
import { Bell, BellRing } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from '@/components/ui/popover';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Badge } from '@/components/ui/badge';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { differenceInDays, parseISO, format } from 'date-fns';
import { ptBR } from 'date-fns/locale';

interface Notification {
  id: string;
  type: 'goal_deadline' | 'goal_achieved' | 'exam_alert';
  title: string;
  message: string;
  date: Date;
  read: boolean;
}

export const NotificationBell = () => {
  const { user } = useAuth();
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    if (user) {
      fetchNotifications();
    }
  }, [user]);

  const fetchNotifications = async () => {
    if (!user) return;

    try {
      // Fetch goals near deadline
      const { data: goals } = await supabase
        .from('health_goals')
        .select('*')
        .eq('user_id', user.id)
        .eq('status', 'active')
        .not('deadline', 'is', null);

      const notifs: Notification[] = [];
      const today = new Date();

      goals?.forEach((goal) => {
        if (!goal.deadline) return;

        const deadline = parseISO(goal.deadline);
        const daysRemaining = differenceInDays(deadline, today);

        if (daysRemaining >= 0 && daysRemaining <= 7) {
          let message = '';
          if (daysRemaining === 0) {
            message = 'Vence hoje! Hora de fazer seu check-up.';
          } else if (daysRemaining === 1) {
            message = 'Vence amanh√£! Agende seu exame.';
          } else {
            message = `Vence em ${daysRemaining} dias (${format(deadline, "d 'de' MMMM", { locale: ptBR })})`;
          }

          notifs.push({
            id: goal.id,
            type: 'goal_deadline',
            title: `Meta: ${goal.exam_name}`,
            message,
            date: deadline,
            read: false,
          });
        }
      });

      // Fetch achieved goals
      const { data: achievedGoals } = await supabase
        .from('health_goals')
        .select('*')
        .eq('user_id', user.id)
        .eq('status', 'achieved')
        .order('updated_at', { ascending: false })
        .limit(5);

      achievedGoals?.forEach((goal) => {
        notifs.push({
          id: `achieved-${goal.id}`,
          type: 'goal_achieved',
          title: `üéâ Meta alcan√ßada!`,
          message: `${goal.exam_name}: ${goal.current_value} ${goal.unit}`,
          date: parseISO(goal.updated_at),
          read: true,
        });
      });

      // Sort by date, newest first
      notifs.sort((a, b) => b.date.getTime() - a.date.getTime());

      setNotifications(notifs);
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

  const unreadCount = notifications.filter(n => !n.read).length;

  const markAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, read: true })));
  };

  return (
    <Popover open={isOpen} onOpenChange={(open) => {
      setIsOpen(open);
      if (open) markAsRead();
    }}>
      <PopoverTrigger asChild>
        <Button
          variant="ghost"
          size="icon"
          className="relative transition-all duration-300 hover:scale-110 min-w-[40px] min-h-[40px] p-2 sm:p-2.5 rounded-xl bg-secondary/50 hover:bg-secondary"
          aria-label="Notifica√ß√µes"
        >
          {unreadCount > 0 ? (
            <BellRing className="h-5 w-5 animate-pulse" />
          ) : (
            <Bell className="h-5 w-5" />
          )}
          {unreadCount > 0 && (
            <Badge
              variant="destructive"
              className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs animate-bounce"
            >
              {unreadCount}
            </Badge>
          )}
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-80 p-0" align="end">
        <div className="p-4 border-b">
          <h4 className="font-semibold">Notifica√ß√µes</h4>
          <p className="text-sm text-muted-foreground">
            {unreadCount > 0 ? `${unreadCount} novas notifica√ß√µes` : 'Nenhuma notifica√ß√£o nova'}
          </p>
        </div>
        <ScrollArea className="h-[300px]">
          {notifications.length === 0 ? (
            <div className="p-4 text-center text-muted-foreground">
              <Bell className="h-8 w-8 mx-auto mb-2 opacity-50" />
              <p>Nenhuma notifica√ß√£o</p>
            </div>
          ) : (
            <div className="divide-y">
              {notifications.map((notif) => (
                <div
                  key={notif.id}
                  className={`p-4 transition-colors hover:bg-muted/50 ${!notif.read ? 'bg-primary/5' : ''}`}
                >
                  <div className="flex items-start gap-3">
                    <div className={`mt-1 h-2 w-2 rounded-full shrink-0 ${notif.type === 'goal_deadline' ? 'bg-yellow-500' :
                        notif.type === 'goal_achieved' ? 'bg-green-500' : 'bg-red-500'
                      }`} />
                    <div className="space-y-1 flex-1">
                      <p className="text-sm font-medium leading-none">{notif.title}</p>
                      <p className="text-sm text-muted-foreground">{notif.message}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </ScrollArea>
      </PopoverContent>
    </Popover>
  );
};

import { useMemo } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import {
    PieChart,
    Pie,
    Cell,
    ResponsiveContainer,
    Legend,
    Tooltip,
} from 'recharts';
import {
    Activity,
    Droplets,
    Heart,
    Zap,
    Shield,
    Stethoscope,
} from 'lucide-react';
import { cn } from '@/lib/utils';

interface CategoryData {
    name: string;
    count: number;
    healthy: number;
    warning: number;
    danger: number;
    icon: React.ElementType;
    color: string;
}

interface ExamResult {
    id: string;
    name: string;
    category: string;
    status: 'healthy' | 'warning' | 'danger';
}

interface HealthCategoryChartProps {
    exams: ExamResult[];
    showLegend?: boolean;
}

const categoryConfig: Record<string, { icon: React.ElementType; color: string }> = {
    'Hemograma': { icon: Droplets, color: '#ef4444' },
    'Bioqu√≠mica': { icon: Zap, color: '#f59e0b' },
    'Horm√¥nios': { icon: Activity, color: '#8b5cf6' },
    'Lip√≠deos': { icon: Heart, color: '#ec4899' },
    'Imunologia': { icon: Shield, color: '#10b981' },
    'Geral': { icon: Stethoscope, color: '#3b82f6' },
};

const statusColors = {
    healthy: '#22c55e',
    warning: '#f59e0b',
    danger: '#ef4444',
};

/**
 * Gr√°fico de distribui√ß√£o de exames por categoria
 */
export const HealthCategoryChart = ({
    exams,
    showLegend = true
}: HealthCategoryChartProps) => {
    const categories = useMemo<CategoryData[]>(() => {
        const categoryMap = new Map<string, CategoryData>();

        exams.forEach(exam => {
            const category = exam.category || 'Geral';
            const config = categoryConfig[category] || categoryConfig['Geral'];

            if (!categoryMap.has(category)) {
                categoryMap.set(category, {
                    name: category,
                    count: 0,
                    healthy: 0,
                    warning: 0,
                    danger: 0,
                    icon: config.icon,
                    color: config.color,
                });
            }

            const data = categoryMap.get(category)!;
            data.count++;
            data[exam.status]++;
        });

        return Array.from(categoryMap.values())
            .sort((a, b) => b.count - a.count);
    }, [exams]);

    const pieData = useMemo(() => {
        return categories.map(cat => ({
            name: cat.name,
            value: cat.count,
            color: cat.color,
        }));
    }, [categories]);

    const statusDistribution = useMemo(() => {
        const totals = { healthy: 0, warning: 0, danger: 0 };
        exams.forEach(exam => {
            totals[exam.status]++;
        });
        return [
            { name: 'Normal', value: totals.healthy, color: statusColors.healthy },
            { name: 'Aten√ß√£o', value: totals.warning, color: statusColors.warning },
            { name: 'Alterado', value: totals.danger, color: statusColors.danger },
        ].filter(d => d.value > 0);
    }, [exams]);

    if (exams.length === 0) {
        return (
            <Card>
                <CardContent className="py-8 text-center text-muted-foreground">
                    Nenhum exame dispon√≠vel para an√°lise.
                </CardContent>
            </Card>
        );
    }

    const CustomTooltip = ({ active, payload }: any) => {
        if (active && payload && payload.length) {
            const data = payload[0].payload;
            return (
                <div className="bg-popover p-3 rounded-lg shadow-lg border border-border">
                    <p className="font-medium text-sm">{data.name}</p>
                    <p className="text-sm text-muted-foreground">
                        {data.value} exame{data.value !== 1 ? 's' : ''}
                    </p>
                </div>
            );
        }
        return null;
    };

    return (
        <Card>
            <CardHeader className="pb-2">
                <CardTitle className="text-lg">Distribui√ß√£o por Categoria</CardTitle>
            </CardHeader>
            <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {/* Category pie chart */}
                    <div>
                        <h4 className="text-sm font-medium text-muted-foreground mb-3 text-center">
                            Por Categoria
                        </h4>
                        <div className="h-[200px]">
                            <ResponsiveContainer width="100%" height="100%">
                                <PieChart>
                                    <Pie
                                        data={pieData}
                                        cx="50%"
                                        cy="50%"
                                        innerRadius={50}
                                        outerRadius={80}
                                        paddingAngle={2}
                                        dataKey="value"
                                    >
                                        {pieData.map((entry, index) => (
                                            <Cell key={`cell-${index}`} fill={entry.color} />
                                        ))}
                                    </Pie>
                                    <Tooltip content={<CustomTooltip />} />
                                </PieChart>
                            </ResponsiveContainer>
                        </div>
                    </div>

                    {/* Status pie chart */}
                    <div>
                        <h4 className="text-sm font-medium text-muted-foreground mb-3 text-center">
                            Por Status
                        </h4>
                        <div className="h-[200px]">
                            <ResponsiveContainer width="100%" height="100%">
                                <PieChart>
                                    <Pie
                                        data={statusDistribution}
                                        cx="50%"
                                        cy="50%"
                                        innerRadius={50}
                                        outerRadius={80}
                                        paddingAngle={2}
                                        dataKey="value"
                                    >
                                        {statusDistribution.map((entry, index) => (
                                            <Cell key={`cell-${index}`} fill={entry.color} />
                                        ))}
                                    </Pie>
                                    <Tooltip content={<CustomTooltip />} />
                                </PieChart>
                            </ResponsiveContainer>
                        </div>
                    </div>
                </div>

                {/* Category legend */}
                {showLegend && (
                    <div className="mt-6 grid grid-cols-2 sm:grid-cols-3 gap-3">
                        {categories.map((category) => (
                            <div
                                key={category.name}
                                className="flex items-center gap-2 p-2 rounded-lg bg-muted/50"
                            >
                                <div
                                    className="h-8 w-8 rounded-lg flex items-center justify-center"
                                    style={{ backgroundColor: `${category.color}20` }}
                                >
                                    <category.icon
                                        className="h-4 w-4"
                                        style={{ color: category.color }}
                                    />
                                </div>
                                <div>
                                    <p className="text-sm font-medium">{category.name}</p>
                                    <div className="flex items-center gap-1 text-xs text-muted-foreground">
                                        <span className="text-status-healthy">{category.healthy}</span>
                                        <span>/</span>
                                        <span className="text-status-warning">{category.warning}</span>
                                        <span>/</span>
                                        <span className="text-status-danger">{category.danger}</span>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </CardContent>
        </Card>
    );
};

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
    Calendar,
    Bell,
    Plus,
    Clock,
    AlertTriangle,
    CheckCircle2,
    Trash2,
} from 'lucide-react';
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
import { format, addMonths, differenceInDays, isPast } from 'date-fns';
import { ptBR } from 'date-fns/locale';
import { cn } from '@/lib/utils';

interface ExamReminder {
    id: string;
    examType: string;
    frequencyMonths: number;
    lastDone: string | null;
    nextDue: string;
    isOverdue: boolean;
    daysUntilDue: number;
}

const frequencyOptions = [
    { value: '3', label: 'A cada 3 meses' },
    { value: '6', label: 'A cada 6 meses' },
    { value: '12', label: 'Anualmente' },
    { value: '24', label: 'A cada 2 anos' },
];

/**
 * Widget de lembretes de exames peri√≥dicos
 */
export const ExamRemindersWidget = () => {
    const { user } = useAuth();
    const { toast } = useToast();
    const [reminders, setReminders] = useState<ExamReminder[]>([]);
    const [loading, setLoading] = useState(true);
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [newExamType, setNewExamType] = useState('');
    const [newFrequency, setNewFrequency] = useState('12');
    const [isSubmitting, setIsSubmitting] = useState(false);

    useEffect(() => {
        if (!user) return;
        fetchReminders();
    }, [user]);

    const fetchReminders = async () => {
        if (!user) return;

        try {
            const { data, error } = await supabase
                .from('exam_reminders')
                .select('*')
                .eq('user_id', user.id)
                .order('next_due', { ascending: true });

            if (error) throw error;

            const mappedReminders: ExamReminder[] = (data || []).map(r => {
                const lastDone = r.last_done ? new Date(r.last_done) : null;
                const nextDue = lastDone
                    ? addMonths(lastDone, r.frequency_months)
                    : new Date();
                const isOverdue = isPast(nextDue);
                const daysUntilDue = differenceInDays(nextDue, new Date());

                return {
                    id: r.id,
                    examType: r.exam_type,
                    frequencyMonths: r.frequency_months,
                    lastDone: r.last_done,
                    nextDue: nextDue.toISOString(),
                    isOverdue,
                    daysUntilDue,
                };
            });

            setReminders(mappedReminders);
        } catch (error) {
            console.error('Error fetching reminders:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleAddReminder = async () => {
        if (!user || !newExamType.trim()) return;

        setIsSubmitting(true);

        try {
            const { error } = await supabase
                .from('exam_reminders')
                .insert({
                    user_id: user.id,
                    exam_type: newExamType.trim(),
                    frequency_months: parseInt(newFrequency),
                    next_due: new Date().toISOString(),
                });

            if (error) throw error;

            toast({
                title: 'Lembrete criado',
                description: `Voc√™ ser√° lembrado de fazer ${newExamType}.`,
            });

            setNewExamType('');
            setNewFrequency('12');
            setIsDialogOpen(false);
            fetchReminders();
        } catch (error) {
            console.error('Error adding reminder:', error);
            toast({
                title: 'Erro',
                description: 'N√£o foi poss√≠vel criar o lembrete.',
                variant: 'destructive',
            });
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleDeleteReminder = async (id: string) => {
        try {
            const { error } = await supabase
                .from('exam_reminders')
                .delete()
                .eq('id', id);

            if (error) throw error;

            setReminders(prev => prev.filter(r => r.id !== id));
            toast({
                title: 'Lembrete removido',
            });
        } catch (error) {
            console.error('Error deleting reminder:', error);
        }
    };

    const handleMarkAsDone = async (id: string) => {
        try {
            const { error } = await supabase
                .from('exam_reminders')
                .update({
                    last_done: new Date().toISOString(),
                })
                .eq('id', id);

            if (error) throw error;

            toast({
                title: 'Exame marcado como realizado',
            });

            fetchReminders();
        } catch (error) {
            console.error('Error updating reminder:', error);
        }
    };

    const getDueStatus = (reminder: ExamReminder) => {
        if (reminder.isOverdue) {
            return { label: 'Atrasado', variant: 'destructive' as const, icon: AlertTriangle };
        }
        if (reminder.daysUntilDue <= 30) {
            return { label: 'Em breve', variant: 'warning' as const, icon: Clock };
        }
        return { label: 'Em dia', variant: 'outline' as const, icon: CheckCircle2 };
    };

    const overdueCount = reminders.filter(r => r.isOverdue).length;
    const upcomingCount = reminders.filter(r => !r.isOverdue && r.daysUntilDue <= 30).length;

    return (
        <Card className="glass-card">
            <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                    <CardTitle className="flex items-center gap-2 text-lg">
                        <Calendar className="h-5 w-5 text-primary" />
                        Lembretes de Exames
                    </CardTitle>
                    <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                        <DialogTrigger asChild>
                            <Button size="sm" className="gap-1">
                                <Plus className="h-4 w-4" />
                                Novo
                            </Button>
                        </DialogTrigger>
                        <DialogContent>
                            <DialogHeader>
                                <DialogTitle>Novo Lembrete</DialogTitle>
                                <DialogDescription>
                                    Configure um lembrete para exames peri√≥dicos.
                                </DialogDescription>
                            </DialogHeader>
                            <div className="space-y-4 py-4">
                                <div className="space-y-2">
                                    <Label htmlFor="exam-type">Tipo de Exame</Label>
                                    <Input
                                        id="exam-type"
                                        value={newExamType}
                                        onChange={(e) => setNewExamType(e.target.value)}
                                        placeholder="Ex: Hemograma completo"
                                    />
                                </div>
                                <div className="space-y-2">
                                    <Label htmlFor="frequency">Frequ√™ncia</Label>
                                    <Select value={newFrequency} onValueChange={setNewFrequency}>
                                        <SelectTrigger id="frequency">
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            {frequencyOptions.map(opt => (
                                                <SelectItem key={opt.value} value={opt.value}>
                                                    {opt.label}
                                                </SelectItem>
                                            ))}
                                        </SelectContent>
                                    </Select>
                                </div>
                            </div>
                            <DialogFooter>
                                <Button
                                    variant="outline"
                                    onClick={() => setIsDialogOpen(false)}
                                >
                                    Cancelar
                                </Button>
                                <Button
                                    onClick={handleAddReminder}
                                    disabled={isSubmitting || !newExamType.trim()}
                                >
                                    Criar Lembrete
                                </Button>
                            </DialogFooter>
                        </DialogContent>
                    </Dialog>
                </div>

                {/* Summary badges */}
                {(overdueCount > 0 || upcomingCount > 0) && (
                    <div className="flex items-center gap-2 mt-2">
                        {overdueCount > 0 && (
                            <Badge variant="destructive" className="gap-1">
                                <AlertTriangle className="h-3 w-3" />
                                {overdueCount} atrasado{overdueCount !== 1 ? 's' : ''}
                            </Badge>
                        )}
                        {upcomingCount > 0 && (
                            <Badge variant="outline" className="gap-1 border-status-warning text-status-warning">
                                <Clock className="h-3 w-3" />
                                {upcomingCount} em breve
                            </Badge>
                        )}
                    </div>
                )}
            </CardHeader>

            <CardContent>
                {loading ? (
                    <div className="space-y-3">
                        {[1, 2, 3].map(i => (
                            <div key={i} className="h-16 bg-muted rounded-lg animate-pulse" />
                        ))}
                    </div>
                ) : reminders.length === 0 ? (
                    <div className="text-center py-6 text-muted-foreground">
                        <Bell className="h-8 w-8 mx-auto mb-2 opacity-50" />
                        <p className="text-sm">Nenhum lembrete configurado.</p>
                        <p className="text-xs mt-1">Crie lembretes para exames peri√≥dicos.</p>
                    </div>
                ) : (
                    <div className="space-y-3">
                        {reminders.map((reminder) => {
                            const status = getDueStatus(reminder);
                            const StatusIcon = status.icon;

                            return (
                                <div
                                    key={reminder.id}
                                    className={cn(
                                        'flex items-center justify-between p-3 rounded-xl border',
                                        reminder.isOverdue && 'bg-status-danger/5 border-status-danger/20',
                                        !reminder.isOverdue && reminder.daysUntilDue <= 30 && 'bg-status-warning/5 border-status-warning/20',
                                        !reminder.isOverdue && reminder.daysUntilDue > 30 && 'bg-muted/50 border-border',
                                    )}
                                >
                                    <div>
                                        <div className="flex items-center gap-2">
                                            <span className="font-medium text-sm">{reminder.examType}</span>
                                            <Badge variant={status.variant} className="gap-1 text-xs">
                                                <StatusIcon className="h-3 w-3" />
                                                {status.label}
                                            </Badge>
                                        </div>
                                        <div className="text-xs text-muted-foreground mt-1">
                                            {reminder.isOverdue ? (
                                                <span className="text-status-danger">
                                                    Atrasado h√° {Math.abs(reminder.daysUntilDue)} dias
                                                </span>
                                            ) : (
                                                <span>
                                                    Pr√≥ximo: {format(new Date(reminder.nextDue), "d 'de' MMMM", { locale: ptBR })}
                                                </span>
                                            )}
                                        </div>
                                    </div>

                                    <div className="flex items-center gap-1">
                                        <Button
                                            variant="ghost"
                                            size="icon"
                                            className="h-8 w-8"
                                            onClick={() => handleMarkAsDone(reminder.id)}
                                            title="Marcar como realizado"
                                        >
                                            <CheckCircle2 className="h-4 w-4 text-status-healthy" />
                                        </Button>
                                        <Button
                                            variant="ghost"
                                            size="icon"
                                            className="h-8 w-8"
                                            onClick={() => handleDeleteReminder(reminder.id)}
                                            title="Remover lembrete"
                                        >
                                            <Trash2 className="h-4 w-4 text-muted-foreground hover:text-destructive" />
                                        </Button>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                )}
            </CardContent>
        </Card>
    );
};

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { cn } from '@/lib/utils';
import { X, Download, ZoomIn, ZoomOut, Loader2, FileText, ExternalLink } from 'lucide-react';
import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';

interface ExamViewerModalProps {
  isOpen: boolean;
  onClose: () => void;
  fileUrl: string | null;
  fileName: string;
}

export const ExamViewerModal = ({ isOpen, onClose, fileUrl, fileName }: ExamViewerModalProps) => {
  const [signedUrl, setSignedUrl] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [zoom, setZoom] = useState(100);

  const isPdf = fileName.toLowerCase().endsWith('.pdf');
  const isImage = /\.(jpg|jpeg|png|gif|webp|bmp)$/i.test(fileName);

  useEffect(() => {
    if (isOpen && fileUrl) {
      fetchSignedUrl();
    } else {
      setSignedUrl(null);
      setError(null);
      setZoom(100);
    }
  }, [isOpen, fileUrl]);

  const fetchSignedUrl = async () => {
    if (!fileUrl) return;

    setLoading(true);
    setError(null);

    try {
      const { data, error: urlError } = await supabase.storage
        .from('exam-files')
        .createSignedUrl(fileUrl, 3600);

      if (urlError) throw urlError;
      setSignedUrl(data.signedUrl);
    } catch (err) {
      console.error('Error getting signed URL:', err);
      setError('N√£o foi poss√≠vel carregar o arquivo');
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async () => {
    if (!signedUrl) return;

    const link = document.createElement('a');
    link.href = signedUrl;
    link.download = fileName;
    link.target = '_blank';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleZoomIn = () => setZoom((prev) => Math.min(prev + 25, 200));
  const handleZoomOut = () => setZoom((prev) => Math.max(prev - 25, 50));

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl h-[85vh] flex flex-col p-0 gap-0 overflow-hidden">
        <DialogHeader className="flex-shrink-0 px-6 py-4 border-b border-border/50 bg-card">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="h-10 w-10 rounded-xl bg-primary/10 flex items-center justify-center">
                <FileText className="h-5 w-5 text-primary" />
              </div>
              <div>
                <DialogTitle className="text-lg font-semibold text-foreground">
                  {fileName}
                </DialogTitle>
                <p className="text-sm text-muted-foreground">
                  {isPdf ? 'Documento PDF' : 'Imagem do exame'}
                </p>
              </div>
            </div>

            <div className="flex items-center gap-2">
              {isImage && signedUrl && (
                <>
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={handleZoomOut}
                    disabled={zoom <= 50}
                    className="h-9 w-9"
                  >
                    <ZoomOut className="h-4 w-4" />
                  </Button>
                  <span className="text-sm text-muted-foreground w-12 text-center">
                    {zoom}%
                  </span>
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={handleZoomIn}
                    disabled={zoom >= 200}
                    className="h-9 w-9"
                  >
                    <ZoomIn className="h-4 w-4" />
                  </Button>
                </>
              )}

              {signedUrl && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleDownload}
                  className="gap-2"
                >
                  <Download className="h-4 w-4" />
                  Baixar
                </Button>
              )}

              {isPdf && signedUrl && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => window.open(signedUrl, '_blank')}
                  className="gap-2"
                >
                  <ExternalLink className="h-4 w-4" />
                  Abrir
                </Button>
              )}
            </div>
          </div>
        </DialogHeader>

        <div className="flex-1 overflow-auto bg-muted/30 p-4">
          {loading && (
            <div className="flex items-center justify-center h-full">
              <div className="flex flex-col items-center gap-3">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
                <p className="text-sm text-muted-foreground">Carregando arquivo...</p>
              </div>
            </div>
          )}

          {error && (
            <div className="flex items-center justify-center h-full">
              <div className="flex flex-col items-center gap-3 text-center">
                <div className="h-16 w-16 rounded-full bg-status-danger/10 flex items-center justify-center">
                  <X className="h-8 w-8 text-status-danger" />
                </div>
                <p className="text-sm text-muted-foreground">{error}</p>
                <Button variant="outline" size="sm" onClick={fetchSignedUrl}>
                  Tentar novamente
                </Button>
              </div>
            </div>
          )}

          {signedUrl && !loading && !error && (
            <>
              {isImage && (
                <div className="flex items-center justify-center min-h-full">
                  <img
                    src={signedUrl}
                    alt={fileName}
                    className="rounded-lg shadow-lg transition-transform duration-300"
                    style={{ 
                      transform: `scale(${zoom / 100})`,
                      maxWidth: '100%',
                      height: 'auto'
                    }}
                  />
                </div>
              )}

              {isPdf && (
                <iframe
                  src={`${signedUrl}#toolbar=1&navpanes=0`}
                  className="w-full h-full rounded-lg border border-border/50"
                  title={fileName}
                />
              )}

              {!isImage && !isPdf && (
                <div className="flex items-center justify-center h-full">
                  <div className="flex flex-col items-center gap-3 text-center">
                    <div className="h-16 w-16 rounded-full bg-muted flex items-center justify-center">
                      <FileText className="h-8 w-8 text-muted-foreground" />
                    </div>
                    <p className="text-sm text-muted-foreground">
                      Visualiza√ß√£o n√£o dispon√≠vel para este tipo de arquivo
                    </p>
                    <Button variant="outline" size="sm" onClick={handleDownload}>
                      <Download className="h-4 w-4 mr-2" />
                      Baixar arquivo
                    </Button>
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
};

import { memo, useState } from 'react';
import { Card } from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Button } from '@/components/ui/button';
import { SearchBar } from './SearchBar';
import { FileSearch } from 'lucide-react';
import { ExamCard } from './ExamCard';
import { ExamViewerModal } from './ExamViewerModal';

interface Exam {
  id: string;
  file_name: string;
  exam_date: string | null;
  lab_name: string | null;
  processed: boolean | null;
  created_at: string;
}

interface ExamsListProps {
  exams: Exam[];
  onExamDeleted?: () => void;
  loading?: boolean;
  filterProps?: {
    filteredData: Exam[];
    setSearchTerm: (term: string) => void;
    filters: { searchTerm: string };
  };
  histories?: any;
}

const ExamsListComponent = ({
  exams,
  onExamDeleted,
  filterProps,
}: ExamsListProps) => {
  const [internalSearch, setInternalSearch] = useState('');
  const [selectedExam, setSelectedExam] = useState<Exam | null>(null);

  // Determine which data and search handler to use
  const displayExams = filterProps?.filteredData || exams;
  const searchTerm = filterProps ? filterProps.filters.searchTerm : internalSearch;
  const handleSearchChange = filterProps ? filterProps.setSearchTerm : setInternalSearch;

  // Filter internally if no filterProps are provided
  const visibleExams = filterProps
    ? displayExams
    : exams.filter(exam =>
      !internalSearch ||
      exam.file_name.toLowerCase().includes(internalSearch.toLowerCase()) ||
      exam.lab_name?.toLowerCase().includes(internalSearch.toLowerCase())
    );

  if (exams.length === 0) {
    return (
      <Card className="p-12">
        <div className="flex flex-col items-center justify-center text-center">
          <div className="rounded-full bg-muted p-6 mb-4">
            <FileSearch className="h-12 w-12 text-muted-foreground" />
          </div>
          <h3 className="text-xl font-semibold mb-2">Nenhum exame encontrado</h3>
          <p className="text-muted-foreground mb-6 max-w-sm">
            Comece fazendo o upload do seu primeiro exame m√©dico.
          </p>
        </div>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {/* Search Bar */}
      {exams.length > 5 && (
        <SearchBar
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Buscar exames..."
        />
      )}

      {/* Exams Grid */}
      <ScrollArea className="h-[calc(100vh-300px)]">
        <div className="grid gap-4 pb-4">
          {visibleExams.length > 0 ? (
            visibleExams.map((exam) => (
              <ExamCard
                key={exam.id}
                exam={exam}
                onView={() => setSelectedExam(exam)}
                onDelete={onExamDeleted || (() => { })}
              />
            ))
          ) : (
            <Card className="p-8">
              <div className="flex flex-col items-center justify-center text-center">
                <FileSearch className="h-8 w-8 text-muted-foreground mb-2" />
                <p className="text-sm text-muted-foreground">
                  Nenhum resultado para "{searchTerm}"
                </p>
                <Button
                  variant="link"
                  onClick={() => handleSearchChange('')}
                  className="mt-2"
                >
                  Limpar busca
                </Button>
              </div>
            </Card>
          )}
        </div>
      </ScrollArea>

      {/* Exam Viewer Modal */}
      <ExamViewerModal
        isOpen={!!selectedExam}
        onClose={() => setSelectedExam(null)}
        fileUrl={selectedExam?.file_name || null} // Assuming file_name is used as path or we need another field? 
        // Wait, ExamViewerCard used fileUrl. Exam interface has file_name. 
        // Usually Supabase storage path matches file_name or there is a file_path field.
        // Let's re-check Exam interface in Index.tsx or useExamData.
        fileName={selectedExam?.file_name || ''}
      />
    </div>
  );
};

export const ExamsList = memo(ExamsListComponent);

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';
import { cn } from '@/lib/utils';
import {
  FileText,
  CheckCircle,
  XCircle,
  Clock,
  RefreshCw,
  Eye,
  ChevronDown,
  History,
  Trash2
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { toast } from '@/hooks/use-toast';
import { ExamViewerModal } from './ExamViewerModal';
import { ConfirmDialog } from '@/components/ui/confirm-dialog';
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from '@/components/ui/collapsible';

interface ExamUpload {
  id: string;
  file_name: string;
  file_url: string | null;
  upload_date: string;
  exam_date: string | null;
  lab_name: string | null;
  processed: boolean | null;
  created_at: string;
}

interface UploadHistoryProps {
  onReprocess?: () => void;
}

export const UploadHistory = ({ onReprocess }: UploadHistoryProps) => {
  const { user } = useAuth();
  const [uploads, setUploads] = useState<ExamUpload[]>([]);
  const [loading, setLoading] = useState(true);
  const [isOpen, setIsOpen] = useState(false);
  const [reprocessingId, setReprocessingId] = useState<string | null>(null);
  const [viewingExam, setViewingExam] = useState<ExamUpload | null>(null);
  const [deletingExam, setDeletingExam] = useState<ExamUpload | null>(null);

  useEffect(() => {
    if (user) {
      fetchUploads();
    }
  }, [user]);

  const fetchUploads = async () => {
    if (!user) return;
    console.log('Fetching uploads for user:', user.id);

    try {
      const { data, error } = await supabase
        .from('exams')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false })
        .limit(20);

      if (error) throw error;
      console.log('Uploads fetched successfully:', data?.length);
      setUploads(data || []);
    } catch (error) {
      console.error('Error fetching uploads:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReprocess = async (e: React.MouseEvent, exam: ExamUpload) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user) return;
    if (!exam.file_url) {
      toast({
        title: 'Erro',
        description: 'Arquivo n√£o encontrado para reprocessar',
        variant: 'destructive',
      });
      return;
    }

    setReprocessingId(exam.id);

    try {
      toast({
        title: 'Reprocessando...',
        description: `O exame ${exam.file_name} est√° sendo reprocessado.`,
      });
      console.log('Reprocessing exam:', exam.id);

      const { data: signedUrlData, error: signedUrlError } = await supabase.storage
        .from('exam-files')
        .createSignedUrl(exam.file_url, 3600);

      if (signedUrlError || !signedUrlData?.signedUrl) {
        throw new Error('Erro ao obter URL do arquivo para reprocessamento.');
      }

      const { data: result, error: functionError } = await supabase.functions.invoke('process-exam', {
        body: {
          fileUrl: signedUrlData.signedUrl,
          fileName: exam.file_name,
          examId: exam.id,
        },
      });

      if (functionError) {
        throw new Error(functionError.message || 'Falha ao reprocessar o exame.');
      }

      toast({
        title: 'Reprocessado!',
        description: 'O exame foi reprocessado com sucesso.',
      });

      await fetchUploads();
      onReprocess?.();
    } catch (error) {
      console.error('Error reprocessing exam:', error);
      toast({
        title: 'Erro ao reprocessar',
        description: error instanceof Error ? error.message : 'N√£o foi poss√≠vel reprocessar o exame. Tente novamente.',
        variant: 'destructive',
      });
    } finally {
      setReprocessingId(null);
    }
  };

  const handleDelete = (e: React.MouseEvent, exam: ExamUpload) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user) {
      toast({
        title: 'Erro de autentica√ß√£o',
        description: 'Voc√™ precisa estar logado para excluir exames.',
        variant: 'destructive',
      });
      return;
    }

    setDeletingExam(exam);
  };

  const performDelete = async () => {
    if (!deletingExam) return;

    try {
      console.log('Attempting to delete exam:', deletingExam.id);
      setLoading(true);

      // 1. Delete file from storage if it exists
      if (deletingExam.file_url) {
        console.log('Deleting file from storage:', deletingExam.file_url);
        const { error: storageError } = await supabase.storage
          .from('exam-files')
          .remove([deletingExam.file_url]);

        if (storageError) {
          console.error('Error deleting file from storage:', storageError);
          // We continue anyway to try and delete the database record
        }
      }

      // 2. Delete from database
      console.log('Deleting record from database:', deletingExam.id);
      const { error: dbError } = await supabase
        .from('exams')
        .delete()
        .eq('id', deletingExam.id);

      if (dbError) {
        console.error('Database delete error:', dbError);
        throw dbError;
      }

      toast({
        title: 'Exame exclu√≠do',
        description: 'O upload e seus resultados foram removidos.',
      });

      console.log('Deletion successful. Refreshing list...');
      await fetchUploads();
      onReprocess?.();
    } catch (error) {
      console.error('Detailed deletion error:', error);
      toast({
        title: 'Erro ao excluir',
        description: error instanceof Error ? error.message : 'N√£o foi poss√≠vel remover o exame.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
      setDeletingExam(null);
    }
  };

  const getStatusIcon = (processed: boolean | null) => {
    if (processed === true) {
      return <CheckCircle className="h-4 w-4 text-status-healthy" />;
    } else if (processed === false) {
      return <XCircle className="h-4 w-4 text-status-danger" />;
    }
    return <Clock className="h-4 w-4 text-status-warning" />;
  };

  const getStatusText = (processed: boolean | null) => {
    if (processed === true) return 'Processado';
    if (processed === false) return 'Falhou';
    return 'Pendente';
  };

  if (loading || uploads.length === 0) return null;

  const failedUploads = uploads.filter(u => u.processed === false);
  const hasFailures = failedUploads.length > 0;

  return (
    <>
      <Collapsible
        open={isOpen}
        onOpenChange={setIsOpen}
        className="rounded-2xl bg-card border border-border/50 shadow-sm overflow-hidden animate-slide-up"
        style={{ animationDelay: '150ms' }}
      >
        <CollapsibleTrigger asChild>
          <button
            className="flex items-center justify-between w-full p-4 hover:bg-accent/30 transition-colors"
            aria-label={isOpen ? "Fechar hist√≥rico de uploads" : "Abrir hist√≥rico de uploads"}
          >
            <div className="flex items-center gap-3">
              <div className={cn(
                'h-10 w-10 rounded-xl flex items-center justify-center',
                hasFailures ? 'bg-status-warning/10' : 'bg-primary/10'
              )}>
                <History className={cn(
                  'h-5 w-5',
                  hasFailures ? 'text-status-warning' : 'text-primary'
                )} />
              </div>
              <div className="text-left">
                <h3 className="font-semibold text-foreground">Hist√≥rico de Uploads</h3>
                <p className="text-sm text-muted-foreground">
                  {uploads.length} upload(s) ‚Ä¢ {failedUploads.length > 0 && (
                    <span className="text-status-danger">{failedUploads.length} com erro</span>
                  )}
                </p>
              </div>
            </div>
            <ChevronDown className={cn(
              'h-5 w-5 text-muted-foreground transition-transform duration-300',
              isOpen && 'rotate-180'
            )} />
          </button>
        </CollapsibleTrigger>

        <CollapsibleContent className="border-t border-border/50">
          <div className="max-h-64 overflow-y-auto">
            {uploads.map((upload) => (
              <div
                key={upload.id}
                className={cn(
                  'flex items-center gap-3 p-3 border-b border-border/30 last:border-b-0 hover:bg-accent/20 transition-colors',
                  upload.processed === false && 'bg-status-danger/5'
                )}
              >
                <div className="h-10 w-10 rounded-lg bg-secondary flex items-center justify-center flex-shrink-0">
                  <FileText className="h-5 w-5 text-muted-foreground" />
                </div>

                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-foreground truncate">
                    {upload.file_name}
                  </p>
                  <div className="flex items-center gap-2 text-xs text-muted-foreground">
                    {getStatusIcon(upload.processed)}
                    <span>{getStatusText(upload.processed)}</span>
                    <span>‚Ä¢</span>
                    <span>
                      {format(new Date(upload.created_at), "dd/MM/yyyy HH:mm", { locale: ptBR })}
                    </span>
                  </div>
                </div>

                <div className="flex items-center gap-2">
                  {upload.file_url && (
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      className="h-8 w-8"
                      onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        setViewingExam(upload);
                      }}
                      aria-label={`Visualizar ${upload.file_name}`}
                    >
                      <Eye className="h-4 w-4" />
                    </Button>
                  )}

                  {(upload.processed === false || upload.processed === null) && (
                    <Button
                      type="button"
                      variant="outline"
                      size="sm"
                      onClick={(e) => handleReprocess(e, upload)}
                      disabled={reprocessingId === upload.id}
                      className="h-8"
                      aria-label={`Reprocessar ${upload.file_name}`}
                    >
                      <RefreshCw className={cn(
                        'h-4 w-4 mr-1',
                        reprocessingId === upload.id && 'animate-spin'
                      )} />
                      {reprocessingId === upload.id ? 'Processando...' : 'Reprocessar'}
                    </Button>
                  )}

                  <Button
                    type="button"
                    variant="ghost"
                    size="icon"
                    className="h-8 w-8 text-muted-foreground hover:text-destructive"
                    onClick={(e) => handleDelete(e, upload)}
                    aria-label={`Excluir ${upload.file_name}`}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            ))}
          </div>
        </CollapsibleContent>
      </Collapsible>

      <ConfirmDialog
        open={!!deletingExam}
        onOpenChange={(open) => !open && setDeletingExam(null)}
        title="Confirmar exclus√£o"
        description={`Deseja realmente excluir "${deletingExam?.file_name}"? Todos os resultados associados ser√£o removidos permanentemente.`}
        confirmText="Excluir"
        cancelText="Cancelar"
        onConfirm={performDelete}
        variant="destructive"
      />

      <ExamViewerModal
        isOpen={!!viewingExam}
        onClose={() => setViewingExam(null)}
        fileUrl={viewingExam?.file_url || null}
        fileName={viewingExam?.file_name || ''}
      />
    </>
  );
};

import { useState, useCallback, useRef, useEffect } from 'react';
import { Upload, FileText, Camera, X, Loader2, CheckCircle, AlertCircle, Image } from 'lucide-react';
import { cn } from '@/lib/utils';
import { useToast } from '@/hooks/use-toast';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/hooks/useAuth';
