import { useState, useEffect, createContext, useContext, ReactNode } from 'react';

interface AccessibilitySettings {
    reducedMotion: boolean;
    highContrast: boolean;
    largeText: boolean;
    screenReaderMode: boolean;
}

interface AccessibilityContextType {
    settings: AccessibilitySettings;
    updateSetting: <K extends keyof AccessibilitySettings>(
        key: K,
        value: AccessibilitySettings[K]
    ) => void;
    resetSettings: () => void;
}

const defaultSettings: AccessibilitySettings = {
    reducedMotion: false,
    highContrast: false,
    largeText: false,
    screenReaderMode: false,
};

const AccessibilityContext = createContext<AccessibilityContextType | null>(null);

const STORAGE_KEY = 'health-beacon-a11y-settings';

/**
 * Provider de acessibilidade
 * Gerencia configura√ß√µes de acessibilidade do usu√°rio
 */
export const AccessibilityProvider = ({ children }: { children: ReactNode }) => {
    const [settings, setSettings] = useState<AccessibilitySettings>(() => {
        // Load from localStorage
        if (typeof window !== 'undefined') {
            const stored = localStorage.getItem(STORAGE_KEY);
            if (stored) {
                try {
                    return { ...defaultSettings, ...JSON.parse(stored) };
                } catch {
                    return defaultSettings;
                }
            }
        }
        return defaultSettings;
    });

    // Detect system preferences
    useEffect(() => {
        if (typeof window === 'undefined') return;

        const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');

        // Only auto-set if user hasn't explicitly set a preference
        const stored = localStorage.getItem(STORAGE_KEY);
        if (!stored && mediaQuery.matches) {
            setSettings(prev => ({ ...prev, reducedMotion: true }));
        }

        const handleChange = (e: MediaQueryListEvent) => {
            if (!localStorage.getItem(STORAGE_KEY)) {
                setSettings(prev => ({ ...prev, reducedMotion: e.matches }));
            }
        };

        mediaQuery.addEventListener('change', handleChange);
        return () => mediaQuery.removeEventListener('change', handleChange);
    }, []);

    // Apply settings to document
    useEffect(() => {
        const html = document.documentElement;

        // Reduced motion
        if (settings.reducedMotion) {
            html.classList.add('reduce-motion');
        } else {
            html.classList.remove('reduce-motion');
        }

        // High contrast
        if (settings.highContrast) {
            html.classList.add('high-contrast');
        } else {
            html.classList.remove('high-contrast');
        }

        // Large text
        if (settings.largeText) {
            html.classList.add('large-text');
        } else {
            html.classList.remove('large-text');
        }

        // Screen reader mode (adds extra context)
        if (settings.screenReaderMode) {
            html.setAttribute('data-screen-reader', 'true');
        } else {
            html.removeAttribute('data-screen-reader');
        }
    }, [settings]);

    // Persist settings
    useEffect(() => {
        localStorage.setItem(STORAGE_KEY, JSON.stringify(settings));
    }, [settings]);

    const updateSetting = <K extends keyof AccessibilitySettings>(
        key: K,
        value: AccessibilitySettings[K]
    ) => {
        setSettings(prev => ({ ...prev, [key]: value }));
    };

    const resetSettings = () => {
        setSettings(defaultSettings);
        localStorage.removeItem(STORAGE_KEY);
    };

    return (
        <AccessibilityContext.Provider value={{ settings, updateSetting, resetSettings }}>
            {children}
        </AccessibilityContext.Provider>
    );
};

/**
 * Hook para acessar configura√ß√µes de acessibilidade
 */
export const useAccessibility = (): AccessibilityContextType => {
    const context = useContext(AccessibilityContext);

    if (!context) {
        throw new Error('useAccessibility must be used within an AccessibilityProvider');
    }

    return context;
};

/**
 * Hook simplificado para verificar prefer√™ncia de movimento reduzido
 */
export const usePrefersReducedMotion = (): boolean => {
    const [prefersReducedMotion, setPrefersReducedMotion] = useState(() => {
        if (typeof window === 'undefined') return false;
        return window.matchMedia('(prefers-reduced-motion: reduce)').matches;
    });

    useEffect(() => {
        if (typeof window === 'undefined') return;

        const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');

        const handleChange = (e: MediaQueryListEvent) => {
            setPrefersReducedMotion(e.matches);
        };

        mediaQuery.addEventListener('change', handleChange);
        return () => mediaQuery.removeEventListener('change', handleChange);
    }, []);

    return prefersReducedMotion;
};

import { useState, useEffect, useCallback } from 'react';
import { useTranslation } from 'react-i18next';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from './useAuth';

export interface Achievement {
    key: string;
    titleKey: string;
    descKey: string;
    icon: string;
    unlockedAt?: string;
    progress?: number;
    target?: number;
}

const ACHIEVEMENT_DEFINITIONS: Omit<Achievement, 'unlockedAt'>[] = [
    {
        key: 'first_exam',
        titleKey: 'achievements.firstExam',
        descKey: 'achievements.firstExamDesc',
        icon: 'üéØ',
        target: 1,
    },
    {
        key: 'complete_profile',
        titleKey: 'achievements.completeProfile',
        descKey: 'achievements.completeProfileDesc',
        icon: 'üë§',
        target: 1,
    },
    {
        key: 'week_streak',
        titleKey: 'achievements.weekStreak',
        descKey: 'achievements.weekStreakDesc',
        icon: 'üî•',
        target: 7,
    },
    {
        key: 'healthy_streak',
        titleKey: 'achievements.healthyStreak',
        descKey: 'achievements.healthyStreakDesc',
        icon: 'üíö',
        target: 30,
    },
    {
        key: 'ten_exams',
        titleKey: 'achievements.tenExams',
        descKey: 'achievements.tenExamsDesc',
        icon: 'üìö',
        target: 10,
    },
];

export const useAchievements = () => {
    const { user } = useAuth();
    const [achievements, setAchievements] = useState<Achievement[]>([]);
    const [loading, setLoading] = useState(true);
    const [unlockedCount, setUnlockedCount] = useState(0);

    const fetchAchievements = useCallback(async () => {
        if (!user) return;

        try {
            // Fetch unlocked achievements from database
            const { data: unlockedData, error } = await supabase
                .from('achievements')
                .select('achievement_key, unlocked_at')
                .eq('user_id', user.id);

            if (error) throw error;

            const unlockedMap = new Map(
                unlockedData?.map((a) => [a.achievement_key, a.unlocked_at]) || []
            );

            // Fetch progress data
            const { data: examsData } = await supabase
                .from('exam_results')
                .select('id')
                .eq('user_id', user.id);

            const examCount = examsData?.length || 0;

            // Merge definitions with unlocked status
            const merged = ACHIEVEMENT_DEFINITIONS.map((def) => ({
                ...def,
                unlockedAt: unlockedMap.get(def.key),
                progress:
                    def.key === 'first_exam'
                        ? Math.min(examCount, 1)
                        : def.key === 'ten_exams'
                            ? Math.min(examCount, 10)
                            : 0,
            }));

            setAchievements(merged);
            setUnlockedCount(unlockedData?.length || 0);
        } catch (error) {
            console.error('Error fetching achievements:', error);
        } finally {
            setLoading(false);
        }
    }, [user]);

    const unlockAchievement = async (key: string) => {
        if (!user) return false;

        try {
            const { error } = await supabase.from('achievements').upsert(
                {
                    user_id: user.id,
                    achievement_key: key,
                    unlocked_at: new Date().toISOString(),
                },
                { onConflict: 'user_id,achievement_key' }
            );

            if (error) throw error;

            await fetchAchievements();
            return true;
        } catch (error) {
            console.error('Error unlocking achievement:', error);
            return false;
        }
    };

    const checkAndUnlockAchievements = useCallback(async () => {
        if (!user) return;

        // Check first exam
        const { data: exams } = await supabase
            .from('exam_results')
            .select('id')
            .eq('user_id', user.id)
            .limit(1);

        if (exams && exams.length > 0) {
            await unlockAchievement('first_exam');
        }

        // Check 10 exams
        const { count } = await supabase
            .from('exam_results')
            .select('id', { count: 'exact', head: true })
            .eq('user_id', user.id);

        if (count && count >= 10) {
            await unlockAchievement('ten_exams');
        }

        // Check complete profile
        const { data: profile } = await supabase
            .from('profiles')
            .select('full_name, birth_date, sex, phone')
            .eq('user_id', user.id)
            .single();

        if (profile?.full_name && profile?.birth_date && profile?.sex && profile?.phone) {
            await unlockAchievement('complete_profile');
        }
    }, [user]);

    useEffect(() => {
        if (user) {
            fetchAchievements();
        }
    }, [user, fetchAchievements]);

    return {
        achievements,
        loading,
        unlockedCount,
        totalCount: ACHIEVEMENT_DEFINITIONS.length,
        refetch: fetchAchievements,
        checkAndUnlockAchievements,
    };
};

import { useState, useEffect, createContext, useContext, ReactNode } from 'react';
import { User, Session } from '@supabase/supabase-js';
import { supabase } from '@/integrations/supabase/client';

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  signUp: (email: string, password: string, fullName?: string) => Promise<{ data: { user: User | null; session: Session | null } | null; error: Error | null }>;
  signIn: (email: string, password: string) => Promise<{ error: Error | null }>;

  signOut: () => Promise<void>;
  resetPassword: (email: string) => Promise<{ error: Error | null }>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider = ({ children }: { children: ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Set up auth state listener FIRST
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      (event, session) => {
        setSession(session);
        setUser(session?.user ?? null);
        setLoading(false);
      }
    );

    // THEN check for existing session
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signUp = async (email: string, password: string, fullName?: string) => {
    const redirectUrl = `${window.location.origin}/auth?type=recovery`;

    const { data, error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        emailRedirectTo: redirectUrl,
        data: {
          full_name: fullName,
        },
      },
    });
    return { data, error };
  };

  const signIn = async (email: string, password: string) => {
    const { error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });
    return { error };
  };

  const resetPassword = async (email: string) => {
    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo: `${window.location.origin}/auth?type=recovery`,
    });
    return { error };
  };

  const signOut = async () => {
    await supabase.auth.signOut();
  };



  return (
    <AuthContext.Provider value={{ user, session, loading, signUp, signIn, signOut, resetPassword }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

import { useState, useCallback, useRef } from 'react';

interface RetryConfig {
    maxRetries?: number;
    initialDelay?: number;
    maxDelay?: number;
    backoffMultiplier?: number;
    retryCondition?: (error: unknown) => boolean;
}

interface RetryState<T> {
    data: T | null;
    error: Error | null;
    isLoading: boolean;
    isRetrying: boolean;
    retryCount: number;
    execute: () => Promise<T | null>;
    reset: () => void;
}

const defaultRetryCondition = (error: unknown): boolean => {
    // Retry on network errors or 5xx server errors
    if (error instanceof TypeError && error.message === 'Failed to fetch') {
        return true;
    }
    if (error instanceof Error && error.message.includes('network')) {
        return true;
    }
    // Check for HTTP status codes in error message
    const statusMatch = String(error).match(/\b(5\d{2})\b/);
    if (statusMatch) {
        return true;
    }
    return false;
};

/**
 * Hook para executar opera√ß√µes ass√≠ncronas com retry autom√°tico
 * Usa exponential backoff para resili√™ncia de rede
 */
export function useRetry<T>(
    asyncFn: () => Promise<T>,
    config: RetryConfig = {}
): RetryState<T> {
    const {
        maxRetries = 3,
        initialDelay = 1000,
        maxDelay = 10000,
        backoffMultiplier = 2,
        retryCondition = defaultRetryCondition,
    } = config;

    const [data, setData] = useState<T | null>(null);
    const [error, setError] = useState<Error | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const [isRetrying, setIsRetrying] = useState(false);
    const [retryCount, setRetryCount] = useState(0);

    const abortControllerRef = useRef<AbortController | null>(null);

    const sleep = (ms: number): Promise<void> =>
        new Promise(resolve => setTimeout(resolve, ms));

    const calculateDelay = (attempt: number): number => {
        const delay = initialDelay * Math.pow(backoffMultiplier, attempt);
        // Add jitter (¬±25%)
        const jitter = delay * 0.25 * (Math.random() * 2 - 1);
        return Math.min(delay + jitter, maxDelay);
    };

    const execute = useCallback(async (): Promise<T | null> => {
        // Cancel any existing request
        if (abortControllerRef.current) {
            abortControllerRef.current.abort();
        }
        abortControllerRef.current = new AbortController();

        setIsLoading(true);
        setError(null);
        setRetryCount(0);

        let lastError: Error | null = null;

        for (let attempt = 0; attempt <= maxRetries; attempt++) {
            try {
                if (attempt > 0) {
                    setIsRetrying(true);
                    setRetryCount(attempt);
                    const delay = calculateDelay(attempt - 1);
                    console.log(`Retry attempt ${attempt}/${maxRetries} after ${Math.round(delay)}ms`);
                    await sleep(delay);
                }

                const result = await asyncFn();
                setData(result);
                setIsLoading(false);
                setIsRetrying(false);
                return result;
            } catch (err) {
                lastError = err instanceof Error ? err : new Error(String(err));

                // Check if we should retry
                if (attempt < maxRetries && retryCondition(err)) {
                    continue;
                }

                // No more retries or condition not met
                break;
            }
        }

        setError(lastError);
        setIsLoading(false);
        setIsRetrying(false);
        return null;
    }, [asyncFn, maxRetries, initialDelay, maxDelay, backoffMultiplier, retryCondition]);

    const reset = useCallback(() => {
        if (abortControllerRef.current) {
            abortControllerRef.current.abort();
        }
        setData(null);
        setError(null);
        setIsLoading(false);
        setIsRetrying(false);
        setRetryCount(0);
    }, []);

    return {
        data,
        error,
        isLoading,
        isRetrying,
        retryCount,
        execute,
        reset,
    };
}

/**
 * Wrapper para fun√ß√µes ass√≠ncronas com retry
 */
export async function withRetry<T>(
    asyncFn: () => Promise<T>,
    config: RetryConfig = {}
): Promise<T> {
    const {
        maxRetries = 3,
        initialDelay = 1000,
        maxDelay = 10000,
        backoffMultiplier = 2,
        retryCondition = defaultRetryCondition,
    } = config;

    const sleep = (ms: number): Promise<void> =>
        new Promise(resolve => setTimeout(resolve, ms));

    const calculateDelay = (attempt: number): number => {
        const delay = initialDelay * Math.pow(backoffMultiplier, attempt);
        const jitter = delay * 0.25 * (Math.random() * 2 - 1);
        return Math.min(delay + jitter, maxDelay);
    };

    let lastError: Error | null = null;

    for (let attempt = 0; attempt <= maxRetries; attempt++) {
        try {
            if (attempt > 0) {
                const delay = calculateDelay(attempt - 1);
                await sleep(delay);
            }
            return await asyncFn();
        } catch (err) {
            lastError = err instanceof Error ? err : new Error(String(err));

            if (attempt < maxRetries && retryCondition(err)) {
                continue;
            }
            break;
        }
    }

    throw lastError;
}

import { useState, useEffect } from 'react';
import { useAuth } from './useAuth';
import { supabase } from '@/integrations/supabase/client';
import type { UserType, Profile } from '@/types/marketplace';

export function useUserType() {
    const { user } = useAuth();
    const [userType, setUserType] = useState<UserType | null>(null);
    const [profile, setProfile] = useState<Profile | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        if (user) {
            loadUserType();
        } else {
            setUserType(null);
            setProfile(null);
            setIsLoading(false);
        }
    }, [user]);

    const loadUserType = async () => {
        if (!user) return;

        setIsLoading(true);
        try {
            const { data, error } = await supabase
                .from('profiles')
                .select('user_type, *')
                .eq('user_id', user.id)
                .single();

            if (error) throw error;

            setUserType(data.user_type);
            setProfile(data);
        } catch (error) {
            console.error('Error loading user type:', error);
            setUserType(null);
            setProfile(null);
        } finally {
            setIsLoading(false);
        }
    };

    return { userType, profile, isLoading, refetch: loadUserType };
}

import { z } from 'zod';

/**
 * Schemas de valida√ß√£o com Zod para dados cr√≠ticos do app
 */

// Schema para perfil de usu√°rio
export const profileSchema = z.object({
    full_name: z.string()
        .min(2, 'Nome deve ter pelo menos 2 caracteres')
        .max(100, 'Nome muito longo')
        .regex(/^[a-zA-Z√Ä-√ø\s'-]+$/, 'Nome cont√©m caracteres inv√°lidos')
        .transform(val => val.trim()),

    birth_date: z.string()
        .refine(val => {
            const date = new Date(val);
            return !isNaN(date.getTime());
        }, 'Data inv√°lida')
        .refine(val => {
            const date = new Date(val);
            const now = new Date();
            return date < now;
        }, 'Data de nascimento deve ser no passado')
        .refine(val => {
            const date = new Date(val);
            const minDate = new Date();
            minDate.setFullYear(minDate.getFullYear() - 150);
            return date > minDate;
        }, 'Data de nascimento inv√°lida'),

    sex: z.enum(['M', 'F', 'O'], {
        errorMap: () => ({ message: 'Sexo deve ser M, F ou O' })
    }).optional(),

    email_notifications: z.boolean().optional().default(true),

    digest_frequency: z.enum(['none', 'weekly', 'monthly'])
        .optional()
        .default('none'),
});

// Schema para resultado de exame
export const examResultSchema = z.object({
    name: z.string()
        .min(1, 'Nome do exame √© obrigat√≥rio')
        .max(200, 'Nome muito longo'),

    value: z.number()
        .or(z.string().transform(val => parseFloat(val)))
        .refine(val => !isNaN(val), 'Valor deve ser num√©rico'),

    unit: z.string()
        .max(50, 'Unidade muito longa'),

    reference_min: z.number().optional().nullable(),
    reference_max: z.number().optional().nullable(),

    status: z.enum(['healthy', 'warning', 'danger']),

    category: z.string().optional().default('Geral'),

    exam_type: z.enum(['laboratory', 'imaging', 'pathology'])
        .optional()
        .default('laboratory'),
});

// Schema para meta de sa√∫de
export const healthGoalSchema = z.object({
    exam_name: z.string()
        .min(1, 'Nome do exame √© obrigat√≥rio'),

    target_value: z.number()
        .positive('Valor alvo deve ser positivo'),

    target_date: z.string()
        .refine(val => {
            const date = new Date(val);
            return !isNaN(date.getTime()) && date > new Date();
        }, 'Data alvo deve ser no futuro'),

    notes: z.string()
        .max(500, 'Notas muito longas')
        .optional(),
});

// Schema para medicamento
export const medicationSchema = z.object({
    name: z.string()
        .min(1, 'Nome do medicamento √© obrigat√≥rio')
        .max(200, 'Nome muito longo'),

    dosage: z.string()
        .min(1, 'Dosagem √© obrigat√≥ria')
        .max(100, 'Dosagem muito longa'),

    frequency: z.string()
        .min(1, 'Frequ√™ncia √© obrigat√≥ria'),

    start_date: z.string()
        .refine(val => !isNaN(new Date(val).getTime()), 'Data inv√°lida'),

    end_date: z.string()
        .refine(val => !isNaN(new Date(val).getTime()), 'Data inv√°lida')
        .optional()
        .nullable(),

    notes: z.string()
        .max(500, 'Notas muito longas')
        .optional(),
});

// Schema para link compartilhado
export const sharedLinkSchema = z.object({
    exam_id: z.string().uuid('ID do exame inv√°lido'),

    expires_at: z.string()
        .refine(val => {
            const date = new Date(val);
            return !isNaN(date.getTime()) && date > new Date();
        }, 'Data de expira√ß√£o deve ser no futuro')
        .optional()
        .nullable(),
});

// Schema para lembrete de exame
export const examReminderSchema = z.object({
    exam_type: z.string()
        .min(1, 'Tipo de exame √© obrigat√≥rio'),

    frequency_months: z.number()
        .int('Frequ√™ncia deve ser n√∫mero inteiro')
        .positive('Frequ√™ncia deve ser positiva')
        .max(60, 'Frequ√™ncia m√°xima √© 60 meses'),

    last_done: z.string()
        .refine(val => !isNaN(new Date(val).getTime()), 'Data inv√°lida')
        .optional()
        .nullable(),
});

/**
 * Valida dados contra um schema
 * Retorna { success: true, data } ou { success: false, errors }
 */
export function validateData<T>(
    schema: z.ZodSchema<T>,
    data: unknown
): { success: true; data: T } | { success: false; errors: string[] } {
    try {
        const result = schema.parse(data);
        return { success: true, data: result };
    } catch (error) {
        if (error instanceof z.ZodError) {
            return {
                success: false,
                errors: error.errors.map(e => `${e.path.join('.')}: ${e.message}`),
            };
        }
        return {
            success: false,
            errors: ['Erro de valida√ß√£o desconhecido'],
        };
    }
}

/**
 * Sanitiza string removendo caracteres perigosos
 */
export function sanitizeString(input: string): string {
    return input
        .replace(/<[^>]*>/g, '') // Remove HTML tags
        .replace(/[<>\"'&]/g, '') // Remove caracteres especiais
        .trim();
}

/**
 * Sanitiza objeto removendo campos n√£o permitidos
 */
export function sanitizeObject<T extends Record<string, unknown>>(
    obj: T,
    allowedFields: (keyof T)[]
): Partial<T> {
    const sanitized: Partial<T> = {};

    for (const field of allowedFields) {
        if (field in obj) {
            const value = obj[field];
            if (typeof value === 'string') {
                sanitized[field] = sanitizeString(value) as T[keyof T];
            } else {
                sanitized[field] = value;
            }
        }
    }

    return sanitized;
}

/**
 * Valida formato de email
 */
export function isValidEmail(email: string): boolean {
    const emailSchema = z.string().email();
    return emailSchema.safeParse(email).success;
}

/**
 * Valida UUID
 */
export function isValidUUID(uuid: string): boolean {
    const uuidSchema = z.string().uuid();
    return uuidSchema.safeParse(uuid).success;
}

